import { Avatar } from '@material-ui/core';
import React from 'react';
import './MessageSender.css';
import VideocamIcon from "@material-ui/icons/Videocam";
import PhotoLibraryIcon from "@material-ui/icons/PhotoLibrary";
import InsertEmoticonIcon from "@material-ui/icons/InsertEmoticon";
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import {useDispatch} from "react-redux";
import {Button, Grid, Input} from "../elements/index";


function MessageSender() {
    // const [input, setInput] = useState('');
    const dispatch = useDispatch();
    const [open, setOpen] = React.useState(false);



    const handleClickOpen = (e) => {
        e.preventDefault();
        setOpen(true);
      };

    const handleClose = (e) => {

        setOpen(false);
      };



    return (
        <div className="messageSender">
            <div className="messageSender__top">
                <Avatar/>
                <form>
                    <button onClick={handleClickOpen} className="messageSender__button">정진우님, 무슨 생각을 하고 계신가요?</button>
                    <div>
                        <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
                            <DialogTitle style={{margin:'auto'}} id="form-dialog-title">게시물 만들기</DialogTitle>
                            <Grid is_flex>
                            <Avatar/>
                            <p>정진우</p>
                            <Button fontSize="13px" width="128.14px" height="30px">친구만</Button>
                            </Grid>
                            <DialogContent>
                            <DialogContentText>
                                To subscribe to this website, please enter your email address here. We will send updates
                                occasionally.
                            </DialogContentText>

                            </DialogContent>
                            <DialogActions>
                            <Button _onClick={handleClose} color="white" backgroundColor="rgb(68,114,227)">
                                게시
                            </Button>

                            </DialogActions>
                        </Dialog>
                     </div>

                    {/* <input 
                        className="messageSender__input"
                        placeholder='무슨 생각을 하고 계신가요?'/> */}
                    {/* <button onClick={handleSubmit} type="submit">
                        Hidden submit
                    </button> */}
                </form>
            </div>
            <div className="messageSender__bottom">
                <div className="messageSender__option">
                <VideocamIcon style={{color:'red'}}/>
                <h3>라이브 방송</h3> 
                </div>

                <div className="messageSender__option">
                <PhotoLibraryIcon style={{color:'green'}}/>
                <h3>사진/동영상</h3> 
                </div>

                <div className="messageSender__option">
                <InsertEmoticonIcon style={{color:'orange'}}/>
                <h3>기분/활동</h3> 
                </div>
            </div>

        </div>
    )
}

export default MessageSender
